import { useState } from 'react';
import { NavLink as RRNavLink, useNavigate } from 'react-router-dom';
import {
    Dropdown,
    DropdownToggle,
    DropdownMenu,
    DropdownItem
} from 'reactstrap';
import {
    Grid,
    ColumnsGap,
    GlobeAsiaAustralia,
    Cup,
    Cookie,
    Palette
} from 'react-bootstrap-icons';

function CheeseDropdown() {
    const [dropdownOpen, setDropdownOpen] = useState(false);
    const toggleDropdown = () => setDropdownOpen(!dropdownOpen);

    const hoverStyle = {
        color: 'black',
    };

    return (
        <Dropdown nav inNavbar isOpen={dropdownOpen} toggle={toggleDropdown}>
            <DropdownToggle nav caret className="link-light bg-dark">
                <Grid className="m-1" size={20} />
                Cheeses by Category
            </DropdownToggle>
            <DropdownMenu className="bg-dark">
                <DropdownItem tag={RRNavLink} to="/category/type" className="text-light" style={{ ':hover': hoverStyle }}>
                    <ColumnsGap className="m-1" size={20} />
                    Cheese By Type
                </DropdownItem>
                <DropdownItem tag={RRNavLink} to="/category/country" className="text-light" style={{ ':hover': hoverStyle }}>
                    <GlobeAsiaAustralia className="m-1" size={20} />
                    Cheese By Country
                </DropdownItem>
                <DropdownItem tag={RRNavLink} to="/category/milk" className="text-light" style={{ ':hover': hoverStyle }}>
                    <Cup className="m-1" size={20} />
                    Cheese By Milk
                </DropdownItem>
                <DropdownItem tag={RRNavLink} to="/category/texture" className="text-light" style={{ ':hover': hoverStyle }}>
                    <Cookie className="m-1" size={20} />
                    Cheese By Texture
                </DropdownItem>
                <DropdownItem tag={RRNavLink} to="/category/color" className="text-light" style={{ ':hover': hoverStyle }}>
                    <Palette className="m-1" size={20} />
                    Cheese By Color
                </DropdownItem>
            </DropdownMenu>
        </Dropdown>
    );
}

export default CheeseDropdown;
