import React from 'react';
import { Card, CardBody, CardTitle, CardText, CardImg, Button } from 'reactstrap';
import { Link } from 'react-router-dom';

const CheeseCard = ({ cheese }) => {
    return (
        <Card className="m-3 shadow-sm" style={{ minWidth: '250px', maxWidth: '300px' }}>
            <CardImg top width="100%" src={cheese.image_url} alt={cheese.type} style={{ height: '200px', objectFit: 'cover' }} />
            <CardBody>
                <CardTitle tag="h5">{cheese.type}</CardTitle>
                <CardText>
                    <strong>Country:</strong> {cheese.country_of_origin} <br />
                    <strong>Price per Kg:</strong> ${cheese.price_per_kilo} <br />
                    <strong>Flavour:</strong> {cheese.flavour} <br />
                </CardText>
                <CardText>{cheese.description}</CardText>
                <Link to={`/cheese/${cheese.id}`}>
                    <Button color="primary">View Details</Button>
                </Link>
            </CardBody>
        </Card>
    );
};

export default CheeseCard;
