import React, { useState } from 'react';
import { Card, CardBody, CardTitle, CardText, CardImg } from 'reactstrap';
import { Link } from 'react-router-dom';
import { Calculator } from 'react-bootstrap-icons';
import CalculatorModal from '../modals/CalculatorModal';
import brokenImage from '../../assets/broken-image.png'; // Placeholder image

const CheeseCard = ({ cheese }) => {
    const [modalOpen, setModalOpen] = useState(false);

    const toggleModal = () => {
        setModalOpen(!modalOpen);
    };

    // Prevent the click on the calculator from triggering the link
    const handleCalculatorClick = (event) => {
        event.stopPropagation(); // Prevent link navigation
        event.preventDefault();  // Prevent link default behavior
        toggleModal();
    };

    // Function to resolve image path with a fallback
    const getImageSrc = (imageUrl) => {
        // try {
        //     return require(`../../assets/cheese/${imageUrl}`).default;  // Correct dynamic image loading
        // } catch (error) {
        //     console.error(`Error loading image: ${imageUrl}. Using fallback.`);
        //     return brokenImage;  // Return placeholder image if the real image is not found
        // }
        return require(`../../assets/cheese/${imageUrl}`).default;  // Correct dynamic image loading
    };

    return (
        <>
            <Link to={`/cheese/${cheese.id}`} style={{ textDecoration: 'none', color: 'inherit' }}>
                <Card className="m-3 shadow-sm" style={{ minWidth: '250px', maxWidth: '300px' }}>
                    <CardImg 
                        top 
                        width="100%" 
                        src={getImageSrc(cheese.image_url)}  // Use the helper function to resolve the image
                        alt={cheese.type} 
                        style={{ height: '200px', objectFit: 'cover' }} 
                    />
                    {console.log('db=' + cheese.image_url + 'the addition' + getImageSrc(cheese.image_url))}
                    <CardBody>
                        <CardTitle tag="h5">{cheese.type}</CardTitle>
                        <CardText>
                            <strong>Country:</strong> {cheese.country_of_origin} <br />
                            <strong>Price per Kg:</strong> ${cheese.price_per_kilo}{' '}
                            <Calculator
                                onClick={handleCalculatorClick}  
                                style={{ cursor: 'pointer' }}
                                size={20}
                                color="gray"
                                className="ms-2"
                            />
                            <br />
                            <strong className="text-nowrap">Flavour:</strong> {cheese.flavour} <br />
                        </CardText>
                        <CardText className="cheese-description">
                            {cheese.description}
                        </CardText>
                    </CardBody>
                </Card>
            </Link>

            {/* Calculator Modal */}
            <CalculatorModal
                isOpen={modalOpen}
                toggle={toggleModal}
                pricePerKg={cheese.price_per_kilo}
            />
        </>
    );
};

export default CheeseCard;
