import { Container, Row } from 'reactstrap';
import HomePageCarousel from '../components/HomePage/HomePageCarousel';
import CheeseCard from '../components/HomePage/CheeseCard';
import logo from '../assets/logo/cheese-logo.png';

// Sample cheese data
const cheeses = [
    {
        id: 1,
        type: 'Cheddar',
        country_of_origin: 'England',
        price_per_kilo: 12.5,
        flavour: 'Sharp and tangy',
        description: 'A popular hard cheese with a sharp, tangy taste.',
        image_url: 'https://via.placeholder.com/300x200?text=Cheddar'
    },
    {
        id: 2,
        type: 'Brie',
        country_of_origin: 'France',
        price_per_kilo: 18.0,
        flavour: 'Mild and buttery',
        description: 'A soft cheese with a buttery and creamy flavor.',
        image_url: 'https://via.placeholder.com/300x200?text=Brie'
    },
    {
        id: 3,
        type: 'Gouda',
        country_of_origin: 'Netherlands',
        price_per_kilo: 15.0,
        flavour: 'Sweet and nutty',
        description: 'A semi-hard cheese with a rich, buttery flavor.',
        image_url: 'https://via.placeholder.com/300x200?text=Gouda'
    }
    // Add more cheeses as needed...
];

function Home() {
    return (
        <Container className="py-5">
            {/* Logo and Heading Section */}
            <div className="row justify-content-center mb-4">
                <div className="col-auto d-flex align-items-center">
                    <img 
                        src={logo} 
                        alt="logo" 
                        className="rounded ms-3 shadow" 
                        width="70" 
                        height="70" 
                        style={{ borderRadius: '50%', border: '2px solid #D6DCDA' }}
                    />
                    <h1 className="m-3 display-4 fw-bold text-dark">
                        PZ Cheeseria
                    </h1>
                    <img 
                        src={logo} 
                        alt="logo" 
                        className="rounded ms-3 shadow" 
                        width="70" 
                        height="70" 
                        style={{ borderRadius: '50%', border: '2px solid #D6DCDA' }}
                    />
                </div>
            </div>

            {/* Subtitle */}
            <h4 className='text-center text-secondary mb-4'>
                Delicious, hand-crafted cheeses delivered to your door.
            </h4>

            {/* Carousel Section */}
            <div className="mb-5">
                <HomePageCarousel />
            </div>

            {/* Cheese Cards Section */}
            <h3 className='text-center mt-5 mb-4'>Our Cheeses</h3>
            <Row className="justify-content-center">
                {cheeses.map((cheese, index) => (
                    <CheeseCard key={index} cheese={cheese} />
                ))}
            </Row>
        </Container>
    );
}

export default Home;
