import React, { useState } from 'react';
import { Modal, ModalHeader, ModalBody, ModalFooter, Button, Input, Label } from 'reactstrap';

const CalculatorModal = ({ isOpen, toggle, pricePerKg }) => {
    const [quantity, setQuantity] = useState(0);
    const [totalCost, setTotalCost] = useState(0);

    const handleCalculate = () => {
        const cost = (quantity / 1000) * pricePerKg; // Convert grams to kilograms
        setTotalCost(cost.toFixed(2)); // Set the total cost
    };

    return (
        <Modal isOpen={isOpen} toggle={toggle}>
            <ModalHeader toggle={toggle}>Calculate Cost</ModalHeader>
            <ModalBody>
                <Label for="quantityInput">Enter quantity (grams):</Label>
                <Input
                    type="number"
                    id="quantityInput"
                    value={quantity}
                    onChange={(e) => setQuantity(e.target.value)}
                    placeholder="Enter grams"
                />
                <p className="mt-3">
                    <strong>Total Cost: </strong>${totalCost}
                </p>
            </ModalBody>
            <ModalFooter>
                <Button color="primary" onClick={handleCalculate}>
                    Calculate
                </Button>{' '}
                <Button color="secondary" onClick={toggle}>
                    Close
                </Button>
            </ModalFooter>
        </Modal>
    );
};

export default CalculatorModal;
