import React, { useEffect, useState } from 'react';
import { Container, Row, Col, Input, InputGroup, InputGroupText, Button, Dropdown, DropdownToggle, DropdownMenu, DropdownItem } from 'reactstrap';
import HomePageCarousel from '../components/homePage/HomePageCarousel';
import CheeseCard from '../components/homePage/CheeseCard';
import { getAllCheeses } from '../api/cheeseApi';
import { Search, SortAlphaDown } from 'react-bootstrap-icons';

function Home() {
    const [cheeses, setCheeses] = useState([]);
    const [filteredCheeses, setFilteredCheeses] = useState([]);
    const [searchQuery, setSearchQuery] = useState('');
    const [sortAlphabetical, setSortAlphabetical] = useState(false);
    const [selectedCategory, setSelectedCategory] = useState('');
    const [isVegetarian, setIsVegetarian] = useState(false);
    const [dropdownOpen, setDropdownOpen] = useState(false);

    useEffect(() => {
        // Fetch cheeses from the API
        const fetchCheeses = async () => {
            try {
                const data = await getAllCheeses();
                setCheeses(data);
                setFilteredCheeses(data); // Initialize filteredCheeses with all cheeses
            } catch (error) {
                console.error('Error fetching cheeses:', error);
            }
        };

        fetchCheeses();
    }, []);

    const toggleDropdown = () => setDropdownOpen(!dropdownOpen);

    // Search and filter function
    const handleSearch = () => {
        const searchResult = cheeses.filter(cheese =>
            cheese.type.toLowerCase().includes(searchQuery.toLowerCase())
        );
        setFilteredCheeses(searchResult);
    };

    // Sort alphabetically
    const handleSortAlphabetically = () => {
        setSortAlphabetical(!sortAlphabetical);
        const sortedCheeses = [...filteredCheeses].sort((a, b) =>
            a.type.localeCompare(b.type)
        );
        setFilteredCheeses(sortedCheeses);
    };

    // Filter by category
    const handleCategoryFilter = (category) => {
        setSelectedCategory(category);
        const filtered = cheeses.filter(cheese => cheese[category]);
        setFilteredCheeses(filtered);
    };

    // Filter by vegetarian
    const handleVegetarianFilter = () => {
        setIsVegetarian(!isVegetarian);
        const filtered = cheeses.filter(cheese => cheese.is_vegetarian);
        setFilteredCheeses(filtered);
    };

    return (
        <Container className="py-5">
            {/* Logo and Heading Section */}
            <div className="row justify-content-center mb-4">
                <div className="col-auto d-flex align-items-center">
                    <img 
                        src="/assets/logo/cheese-logo.png"
                        alt="logo"
                        className="rounded ms-3 shadow"
                        width="70"
                        height="70"
                        style={{ borderRadius: '50%', border: '2px solid #D6DCDA' }}
                    />
                    <h1 className="m-3 display-4 fw-bold text-dark">
                        PZ Cheeseria
                    </h1>
                    <img 
                        src="/assets/logo/cheese-logo.png"
                        alt="logo"
                        className="rounded ms-3 shadow"
                        width="70"
                        height="70"
                        style={{ borderRadius: '50%', border: '2px solid #D6DCDA' }}
                    />
                </div>
            </div>

            {/* Subtitle */}
            <h4 className='text-center text-secondary mb-4'>
                Delicious, hand-crafted cheeses delivered to your door.
            </h4>

            {/* Sort/Filter Section */}
            <div className="mb-4">
                <Row>
                    <Col md="3">
                        <Button onClick={handleSortAlphabetically}>
                            <SortAlphaDown size={20} /> Alphabetical
                        </Button>
                    </Col>
                    <Col md="3">
                        <Dropdown isOpen={dropdownOpen} toggle={toggleDropdown}>
                            <DropdownToggle caret>
                                Filter by Category
                            </DropdownToggle>
                            <DropdownMenu>
                                <DropdownItem onClick={() => handleCategoryFilter('type')}>
                                    Cheese by Type
                                </DropdownItem>
                                <DropdownItem onClick={() => handleCategoryFilter('country_of_origin')}>
                                    Cheese by Country
                                </DropdownItem>
                                <DropdownItem onClick={() => handleCategoryFilter('made_from')}>
                                    Cheese by Milk
                                </DropdownItem>
                                <DropdownItem onClick={() => handleCategoryFilter('texture')}>
                                    Cheese by Texture
                                </DropdownItem>
                                <DropdownItem onClick={() => handleCategoryFilter('color')}>
                                    Cheese by Colour
                                </DropdownItem>
                            </DropdownMenu>
                        </Dropdown>
                    </Col>
                    <Col md="3">
                        <Button onClick={handleVegetarianFilter}>
                            Vegetarian Cheeses
                        </Button>
                    </Col>
                    <Col md="3">
                        <InputGroup>
                            <Input
                                type="search"
                                value={searchQuery}
                                onChange={(e) => setSearchQuery(e.target.value)}
                                placeholder="Search Cheeses..."
                            />
                            <InputGroupText onClick={handleSearch} style={{ cursor: 'pointer' }}>
                                <Search size={20} />
                            </InputGroupText>
                        </InputGroup>
                    </Col>
                </Row>
            </div>

            {/* Cheese Cards Section */}
            <h3 className='text-center mt-5 mb-4'>Our Cheeses</h3>
            <Row className="justify-content-center">
                {filteredCheeses.length > 0 ? (
                    filteredCheeses.map((cheese, index) => (
                        <Col key={index} xs="12" sm="6" md="4" lg="3" className="d-flex justify-content-center mb-4">
                            <CheeseCard cheese={cheese} />
                        </Col>
                    ))
                ) : (
                    <p className="text-center">No cheeses available at the moment.</p>
                )}
            </Row>
        </Container>
    );
}

export default Home;
