import { Container, Button } from 'reactstrap';
import HomePageCarousel from '../components/HomePage/HomePageCarousel';
import logo from '../assets/logo/cheese-logo.png';

function Home() {
    return (
        <Container className="py-5">
            {/* Logo and Heading Section */}
            <div className="row justify-content-center mb-4">
                <div className="col-auto d-flex align-items-center">
                    <img 
                        src={logo} 
                        alt="logo" 
                        className="rounded ms-3 shadow" 
                        width="70" 
                        height="70" 
                        style={{ borderRadius: '50%', border: '2px solid #D6DCDA' }}
                    />
                    <h1 className="m-3 display-4 fw-bold text-dark">
                        PZ Cheeseria
                    </h1>
                    <img 
                        src={logo} 
                        alt="logo" 
                        className="rounded ms-3 shadow" 
                        width="70" 
                        height="70" 
                        style={{ borderRadius: '50%', border: '2px solid #D6DCDA' }}
                    />
                </div>
            </div>

            {/* Subtitle */}
            <h4 className='text-center text-secondary mb-4'>
                Delicious, hand-crafted cheeses delivered to your door.
            </h4>

            {/* Carousel Section */}
            <div className="mb-5">
                <HomePageCarousel />
            </div>

            {/* Call to Action Button */}
            <div className="text-center mt-5">
                <Button 
                    color="primary" 
                    size="lg" 
                    className="shadow-sm"
                    style={{
                        borderRadius: '30px',
                        padding: '10px 30px',
                        background: 'linear-gradient(90deg, #ff8a00, #e52e71)'
                    }}
                >
                    Shop Now
                </Button>
            </div>

            {/* Footer */}
            <div className="row justify-content-center mt-5 text-center">
                <div className="col-md-6">
                    <p className="text-muted">
                        We offer a wide range of artisan cheeses, crafted with love and tradition. Whether you're a cheese connoisseur or looking to discover new flavors, PZ Cheeseria has something for everyone.
                    </p>
                </div>
            </div>
        </Container>
    );
}

export default Home;
